    private String mucTitle;
    private String mucPic;
    private String version;*/

        muc_name,


        muc_desc,


        show_name,


        muc_title,


        muc_pic,




        #{mucName},


        #{mucDesc},


        #{showName},


        #{mucTitle},


        #{mucPic},
